import { useDebouncedValue } from '@mantine/hooks';
import { create } from 'zustand';

export type GPTMessage = {
  role: string;
  content: string;
};

export type Manual = {
  id: string;
  usage: string;
  content: string;
};

export type Strategy = {
  id: string;
  usage: string;
  content: string;
};


export type GPTMode = 'QUALITY' | 'FAST';

type PromptStore = {
  strategy?: Strategy;
  mode: GPTMode;
  manuals: Manual[];
  messages: GPTMessage[];
  setMessages: (newMessages: GPTMessage[]) => void;
  removeMessage: (index: number) => void;

  usedTokens: number;
  availableTokens: number;

  promptHistory: string[];
  promptIndex: number;

  historyUp: () => void;
  historyDown: () => void;
  newPrompt: () => void;
  editPrompt: (prompt: string) => void;
  getPrompt: () => string;

  requestAnalysis: () => void;
  doAnalysis: () => void;
  analysisTimeoutId: NodeJS.Timeout | undefined;

  doGPT: () => void;

  messagesWithPrompt: () => GPTMessage[];
};

export const usePromptStore = create<PromptStore>((set, get) => ({
  strategy: undefined,
  mode: 'QUALITY',
  manuals: [],
  messages: [],
  setMessages: (newMessages: GPTMessage[]) =>
    set((state) => ({
      messages: newMessages,
    })),
  removeMessage: (index: number) =>
    set((state) => ({
      messages: state.messages.filter((_, i) => i !== index),
    })),

  messagesWithPrompt: () => {
    return [...get().messages, { role: 'user', content: get().getPrompt() }];
  },

  doAnalysis: async () => {
    let response = await fetch(`http://${window.location.hostname}:8000/analyse`, {
      method: 'POST',
      body: JSON.stringify({
        messages: get().messagesWithPrompt(),
        mode: get().mode,
      }),
      headers: {
        'Content-Type': 'application/json',
      },
    });

    let data = await response.json();

    set((state) => ({
      strategy: data.strategy,
      manuals: data.manuals,
      usedTokens: data.usedTokens,
      availableTokens: data.availableTokens,
    }));
  },

  doGPT: async () => {
    let response = await fetch(`http://${window.location.hostname}:8000/gpt`, {
      method: 'POST',
      body: JSON.stringify({
        messages: get().messages,
        mode: get().mode
      }),
      headers: {
        'Content-Type': 'application/json',
      },
    });

    const reader = response.body?.getReader();
    const decoder = new TextDecoder('utf-8');

    let newMessages = [...get().messages, { role: 'assistant', content: '' }];

    while (true) {
      const { value, done } = (await reader?.read()) || { value: undefined, done: true };

      let text = decoder.decode(value);
      if (text.includes('§§§§§CLEAR§§§§§')) {
        newMessages[newMessages.length - 1].content = '';
        //delete everything up to $$$CLEAR$$$ in text
        text = text.substring(text.indexOf('§§§§§CLEAR§§§§§') + 15);
      }

      newMessages[newMessages.length - 1].content += text;
      get().setMessages(newMessages.slice());
      //console.log(newMessages);

      if (done) {
        break;
      }
    }
  },

  getPrompt: () => {
    return get().promptHistory[get().promptIndex];
  },

  analysisTimeoutId: undefined,
  requestAnalysis: () => {
    if (get().analysisTimeoutId) {
      clearTimeout(get().analysisTimeoutId);
    }

    const timeoutId = setTimeout(() => {
      get().doAnalysis();
    }, 500);

    set((state) => ({
      analysisTimeoutId: timeoutId,
    }));
  },

  usedTokens: 0,
  availableTokens: 0,
  promptHistory: [''],
  promptIndex: 0,
  historyUp: () =>
    set((state) => ({
      promptIndex: Math.min(state.promptHistory.length - 1, state.promptIndex + 1),
    })),
  historyDown: () => set((state) => ({ promptIndex: Math.max(0, state.promptIndex - 1) })),
  editPrompt: (prompt) => {
    set((state) => ({
      promptHistory: [
        ...state.promptHistory.slice(0, state.promptIndex),
        prompt,
        ...state.promptHistory.slice(state.promptIndex + 1, state.promptHistory.length),
      ],
    }));
    get().requestAnalysis();
  },
  newPrompt: () =>
    set((state) => ({
      promptHistory: ['', ...state.promptHistory],
      promptIndex: 0,
    })),
}));

export function useDebouncedPrompt() {
  const prompt = usePromptStore((state) => state.promptHistory[state.promptIndex]).trim();
  const [debouncedPrompt] = useDebouncedValue(prompt, 150, { leading: true });

  return debouncedPrompt;
}
