import { useCallback, useState, useRef, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import CodeBlock from './code_block';
import { CommandInput } from './components/command_input';
//import "highlight.js/styles/dracula.css"; // use dracula.css as highlight.js theme

type Message = {
  role: string;
  content: string;
};

function App() {
  const [messages, setMessages] = useState<Message[]>([]);

  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollTop = Math.max(
        messagesEndRef.current.scrollHeight - messagesEndRef.current.clientHeight,
        0,
      );
    }
  }, [messages]);
  const handleSubmit = useCallback(
    async (prompt: string) => {
      let newMessages = [...messages, { role: 'user', content: prompt }];
      setMessages(newMessages);

      let postRequest = async (messages: Message[]) => {
        console.log(messages);
        return await fetch(`http://${window.location.hostname}:8000/gpt4`, {
          method: 'POST',
          body: JSON.stringify({
            messages: messages,
            model_type: 'gpt-4',
          }),
          headers: {
            'Content-Type': 'application/json',
          },
        });
      };

      let handleResponse = async (messages: Message[], response: any) => {
        const reader = response.body.getReader();
        const decoder = new TextDecoder('utf-8');

        let newMessages = [...messages, { role: 'assistant', content: '' }];

        while (true) {
          const { value, done } = await reader.read();

          const text = decoder.decode(value);
          newMessages[newMessages.length - 1].content += text;
          setMessages(newMessages.slice());
          console.log(newMessages);

          if (done) {
            break;
          }
        }
      };

      let response = await postRequest(newMessages);

      await handleResponse(newMessages, response);
    },
    [messages],
  );

  return (
    <div className="App flex flex-col fixed top-0 left-0 bottom-0 right-0 p-5 justify-between">
      <div className="flex-grow overflow-y-auto flex flex-col" ref={messagesEndRef}>
        {messages.length === 0 ? (
          <div>No messages</div>
        ) : (
          <div>
            {messages.map((message, index) => (
              <div
                key={index}
                className="max-w-900px w-full px-5 py-3 rounded-lg bg-black-alpha-70 backdrop-blur-sm border-black-alpha-40 shadow-lg "
              >
                <ReactMarkdown
                  components={{
                    code: ({ node, ...props }) => (
                      <CodeBlock
                        language={props.lang}
                        val={node.children.map((e) => (e as any).value).join(' ')}
                      />
                    ),
                  }}
                >
                  {message.content}
                </ReactMarkdown>
              </div>
            ))}
          </div>
        )}
      </div>
      <div className="">
        <CommandInput onSubmit={handleSubmit} />
      </div>
    </div>
  );
}

export default App;
