import { useCallback, useState, useRef, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import CodeBlock from './code_block';
import { CommandInput } from './components/command_input';
import TrashIcon from '@heroicons/react/24/solid/TrashIcon';
//import "highlight.js/styles/dracula.css"; // use dracula.css as highlight.js theme

type Message = {
  role: string;
  content: string;
};

function App() {
  const [messages, setMessages] = useState<Message[]>([]);
  const [scrolling, setScrolling] = useState<boolean>(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const timerIdRef = useRef<NodeJS.Timeout | null>(null);  
  useEffect(() => {
    const { current } = messagesEndRef;
    if (!current) return;

    const handleScroll = () => {
      // If a timer already exists from a previous scroll event, clear it.
      if (timerIdRef.current) {
        clearTimeout(timerIdRef.current);
      }
      setScrolling(true);
      timerIdRef.current = setTimeout(() => setScrolling(false), 1000); // Reset scrolling after 1 second.
    };

    current.addEventListener('scroll', handleScroll);
    return () => {
      current.removeEventListener('scroll', handleScroll);
      // It's important to also clear the timer when the component unmounts.
      if (timerIdRef.current) {
        clearTimeout(timerIdRef.current);
      }
    };
  }, []);

  useEffect(() => {
    const { current } = messagesEndRef;
    if (!current || scrolling) return; // Check if scrolling is true, if so return without updating scrollTop

    if (Math.abs(current.scrollTop - (current.scrollHeight - current.clientHeight)) < 300) {
      let newValue = Math.max(current.scrollHeight - current.clientHeight, 0);
      current.scrollTop = newValue;
    }
  }, [messages, scrolling]); // Added scrolling as a dependency.

  const removeMessage = (index: number) => {
    setMessages((oldMessages) => {
      return messages.filter((_, i) => i !== index);
    });
  };

  const handleSubmit = useCallback(
    async (prompt: string) => {
      let newMessages = [...messages, { role: 'user', content: prompt }];
      setMessages(newMessages);

      let postRequest = async (messages: Message[]) => {
        return await fetch(`http://${window.location.hostname}:8000/gpt`, {
          method: 'POST',
          body: JSON.stringify({
            messages: messages,
            model: 'gpt-4',
          }),
          headers: {
            'Content-Type': 'application/json',
          },
        });
      };

      let handleResponse = async (messages: Message[], response: any) => {
        const reader = response.body.getReader();
        const decoder = new TextDecoder('utf-8');

        let newMessages = [...messages, { role: 'assistant', content: '' }];

        while (true) {
          const { value, done } = await reader.read();
          let text = decoder.decode(value);
          if (text.includes('§§§§§CLEAR§§§§§')) {
            newMessages[newMessages.length - 1].content = '';
            //delete everything up to $$$CLEAR$$$ in text
            text = text.substring(text.indexOf('§§§§§CLEAR§§§§§') + 15);
          }

          newMessages[newMessages.length - 1].content += text;
          setMessages(newMessages.slice());
          //console.log(newMessages);

          if (done) {
            break;
          }
        }
      };

      let response = await postRequest(newMessages);

      await handleResponse(newMessages, response);
    },
    [messages],
  );

  return (
    <div className="App flex flex-col fixed top-0 left-0 bottom-0 right-0 p-5 justify-between">
      <div className="flex-grow overflow-y-auto flex flex-col" ref={messagesEndRef}>
        {messages.length === 0 ? (
          <div>No messages</div>
        ) : (
          <div>
            {messages.map((message, index) => (
              <div
                key={index}
                className="flex justify-between items-center max-w-900px w-full px-5 py-5 rounded-lg bg-slate-700  shadow-lg mb-5 "
              >
                <div className="prose prose-stone dark:prose-invert">
                  <ReactMarkdown
                    components={{
                      code: ({ node, ...props }) => (
                        <CodeBlock
                          language={props.lang}
                          val={node.children.map((e) => (e as any).value).join(' ')}
                        />
                      ),
                    }}
                  >
                    {message.content}
                  </ReactMarkdown>
                </div>
                <button onClick={() => removeMessage(index)} className=" self-start">
                  <TrashIcon className="h-5 w-5 text-white" />{' '}
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
      <div className="">
        <CommandInput onSubmit={handleSubmit} />
      </div>
    </div>
  );
}

export default App;
