import { PaperAirplaneIcon } from '@heroicons/react/24/solid';
import { useRef, useState } from 'react';

import { usePromptStore } from '../workspace/prompt-store';
import { cn } from '../utils/styles';

interface MessageInputProps {
  onSubmit: (command: string) => void;
  className?: string;
}

export const CommandInput = ({ onSubmit, className }: MessageInputProps) => {
  const prompt = usePromptStore((state) => state.prompt);
  const setPrompt = usePromptStore((state) => state.setPrompt);
  const textAreaRef = useRef<HTMLTextAreaElement>(null);

  // A state variable to hold the history of messages sent
  const [messageHistory, setMessageHistory] = useState<string[]>([]);
  // A state variable to keep track of the current position in the message history
  const [messageIndex, setMessageIndex] = useState<number | null>(null);


  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setPrompt(e.target.value);




    if (textAreaRef.current) {
        if (e.target.value.trim() === '') {

          // Otherwise, set its height to the line height
      // Fetching the computed styles of textarea
      const computedStyles = window.getComputedStyle(textAreaRef.current);

      // Fetching lineHeight value in pixels
      const lineHeightPixels = parseInt(computedStyles.lineHeight);

      // Is NaN check is important in case lineHeight is normal or has relative values
      if (isNaN(lineHeightPixels)) {
        textAreaRef.current.style.height = 'auto';
      } else {
        textAreaRef.current.style.height = `${lineHeightPixels * textAreaRef.current.value.split('\n').length}px`;
      }

        } else {
          textAreaRef.current.style.height = `${textAreaRef.current.scrollHeight}px`;
        }
    }
  };


  const handleKeyDown = async (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      if (!disabled) await handleSendMessage();
    }

    if(e.key === 'ArrowUp' && messageHistory[messageIndex+1]) {
      setMessageIndex(messageIndex+1 || 0)
    } else if (e.key === 'ArrowDown' && messageIndex > 0) {
      setMessageIndex(messageIndex-1)
    }

    messageIndex !== null && setPrompt(messageHistory[messageIndex])
  };

  const handleSendMessage = async () => {
    const trimmed = prompt.trim();
    if (!trimmed) return;
    setPrompt('');
    onSubmit(trimmed);

    // Add the sent message to the beginning of the history array
    setMessageHistory([trimmed, ...messageHistory]);
    setMessageIndex(null);
  };

  const disabled = !prompt.trim();

  return (
    <div className={cn(className, "flex w-full flex-col p-4")}>
      <div className="flex items-center">
        <textarea
          ref={textAreaRef}
          className="border-slate-300 bg-slate-400 text-slate-900 flex-grow resize-none overflow-hidden rounded-md border px-4 py-2 focus:outline-none focus:ring-2"
          value={prompt}
          onChange={handleChange}
          onKeyUp={handleKeyUp}
          onKeyDown={handleKeyDown}
          placeholder="Type a new command here..."
          rows={1}
          style={{ boxSizing: 'border-box', transition: 'height 0.5s' }}
        />
        <button
          className={cn(
            'focus:ring-primary ml-4 rounded-md px-4 py-2 transition-colors duration-200 ease-in-out focus:outline-none focus:ring-4',
            {
              'bg-slate-500  text-slate-800 cursor-not-allowed': disabled,
              'bg-orange-700 hover:bg-orange-500 text-primary-content': !disabled,
            },
          )}
          type="button"
          onClick={handleSendMessage}
          disabled={disabled}
        >
          <PaperAirplaneIcon className="h-6 w-6 scale-75" />
        </button>
      </div>
    </div>
  );
};
