import { useDebouncedValue } from '@mantine/hooks';
import { create } from 'zustand';

export type GPTMessage = {
  role: string;
  content: string;
};

export type Manual = {
  name: string;
  usage: string;
  content: string;
}

export type GPTMode = "QUALITY" | "FAST"

type PromptStore = {
  strategy: string;
  mode: GPTMode;
  manuals: Manual[];
  messages: GPTMessage[];
  setMessages: (newMessages: GPTMessage[]) => void;
  removeMessage: (index: number) => void;

  usedTokens: number,
  availableTokens: number,

  promptHistory: string[];
  promptIndex: number;

  historyUp: () => void;
  historyDown: () => void;
  newPrompt: () => void;
  editPrompt: (prompt: string) => void;
};

export const usePromptStore = create<PromptStore>((set, get) => ({
  strategy: 'Strat1',
  mode: "QUALITY",
  manuals: [
    {
      name: "Linkedin",
      usage: "Usage",
      content: "Content",
    }
  ],
  messages: [],
  setMessages: (newMessages: GPTMessage[]) =>
    set((state) => ({
      messages: newMessages
    })),
  removeMessage: (index: number) => 
    set((state) => ({
      messages: state.messages.filter((_, i) => i !== index)
    })),

  doAnalysis: () => {
    fetch('/analyse', {
      body: JSON.stringify({
        messages: get().messages,
        mode: get().mode,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        set((state) => ({
          strategy: data.strategy,
          manuals: data.manuals,
          usedTokens: data.usedTokens,
          availableTokens: data.availableTokens,
        }));
      });
  },
  requestAnalysis: () => {

  },

  usedTokens: 32,
  availableTokens: 8000,
  promptHistory: [''],
  promptIndex: 0,
  historyUp: () => set((state) => ({ promptIndex: Math.min(state.promptHistory.length - 1, state.promptIndex + 1) })),
  historyDown: () => set((state) => ({ promptIndex: Math.max(0, state.promptIndex - 1) })),
  editPrompt: (prompt) =>
    set((state) => ({
      promptHistory: [...state.promptHistory.slice(0, state.promptIndex),  prompt, ...state.promptHistory.slice(state.promptIndex + 1, state.promptHistory.length)],
    })),
  newPrompt: () =>
    set((state) => ({
      promptHistory: ['', ...state.promptHistory],
      promptIndex: 0,
    })),
}));

export function usePrompt() {
  return usePromptStore((state) => state.promptHistory[state.promptIndex]);
}

export function useDebouncedPrompt() {
  const prompt = usePromptStore((state) => state.promptHistory[state.promptIndex]).trim();
  const [debouncedPrompt] = useDebouncedValue(prompt, 150, { leading: true });

  return debouncedPrompt;
}
